import React, { useState } from 'react';

const Achievements = () => {
  const [achievementImages, setAchievementImages] = useState({});

  const achievements = [
    {
      id: 1,
      title: "IBM Z Datathon 2025 - A Global Datathon Event",
      description: "Recognized for outstanding contribution to full-stack development"
    },
    {
      id: 2,
      title: "Hackathon Winner",
      description: "First place in National Coding Competition"
    },
    {
      id: 3,
      title: "Open Source Contributor",
      description: "Active contributor to popular React libraries"
    }
  ];

  const handleImageUpload = (e, achievementId) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setAchievementImages(prev => ({
          ...prev,
          [achievementId]: e.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <section id="achievements" className="achievements">
      <div className="container">
        <h2>Achievements</h2>
        <div className="achievements-grid">
          {achievements.map((achievement) => (
            <div key={achievement.id} className="achievement-card">
              <div className="achievement-image-container">
                {achievementImages[achievement.id] ? (
                  <img src={achievementImages[achievement.id]} alt={achievement.title} className="achievement-img" />
                ) : (
                  <div className="achievement-placeholder">Achievement Photo</div>
                )}
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => handleImageUpload(e, achievement.id)}
                  className="achievement-image-upload"
                  id={`achievement-upload-${achievement.id}`}
                />
                <label htmlFor={`achievement-upload-${achievement.id}`} className="achievement-upload-btn">
                  Upload Photo
                </label>
              </div>
              <h3>{achievement.title}</h3>
              <p>{achievement.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Achievements;
